import styled from "react-emotion";

const colorMap = {
  primary: "#5978f3",
  secondary: "rgba(0, 0, 0, 0.05)"
};

const border = {
  primary: 'none',
  secondary: '1px solid rgba(0, 0, 0, 0.12)',
}

export default styled("button")`
  background-color: ${props => colorMap[props.color || "primary"]};
  height 40px;
  border-radius: .25rem;
  font-weight: 700;
  color: white;
  cursor: pointer;
  border: ${props => colorMap[props.border || "primary"]};
  padding: 11px 16px;
  font-size: 16px;
  &: hover {
    background-color: #6f8bfb;
  }
`;
