import React from "react";
import { css } from "emotion";
import Friends from "../../icons/Friends";
import Link from "../../core/Link";
import Button from "../../core/Button";
import Paragraph from "../../core/Paragraph";
import MaxWidth from "../../core/MaxWidth";
import Card from "../../core/Card";
import CardBody from "../../core/CardBody";
import { Flex, FlexItem } from "../../core/Flex";
import InputWithLabel from "../../core/InputWithLabel";

function Auth({ children }) {
  return (
    <MaxWidth maxWidth="770" className={css({ width: "100%" })}>
      <Flex>
        <h1
          className={css({
            fontSize: 24,
            lineHeight: "32px",
            margin: "0 0 16px"
          })}
        >
          Login to{" "}
          <span className={css({ color: "#405dcf", fontSize: 32 })}>
            clique.
          </span>
        </h1>
      </Flex>

      <Flex>
        <FlexItem>
          <InputWithLabel type="text" label="Email" />
          <InputWithLabel type="text" label="Password" />

          <Button className={css({ width: "100%", marginTop: 12 })}>
            Log In
          </Button>
          <Paragraph
            className={css({
              margin: "16px 0"
            })}
          >
            <Link href="/accounts/reset-password">
              Having trouble logging in?
            </Link>
          </Paragraph>
          <Button color="secondary" className={css({ width: "100%" })}>
            Need to create a new account?
          </Button>
        </FlexItem>

        <FlexItem className={css({ paddingLeft: 24 })}>
          <Card>
            <CardBody>
              <Friends />
              <article
                className={css({
                  maxWidth: 280,
                  margin: "24px auto 0"
                })}
              >
                <Paragraph
                  className={css({
                    marginBottom: 8,
                    textAlign: "center"
                  })}
                >
                  Clique helps you plan events with your friends.
                </Paragraph>
                <Paragraph
                  className={css({
                    color: "rgba(0, 0, 0, 0.56)",
                    textAlign: "center"
                  })}
                >
                  Login, create an event, and start planning get togethers
                  easily.
                </Paragraph>
              </article>
            </CardBody>
          </Card>
        </FlexItem>
      </Flex>
    </MaxWidth>
  );
}

export function Login() {
  return (
    <MaxWidth maxWidth="770" className={css({ width: "100%" })}>
      <Flex>
        <h1
          className={css({
            fontSize: 24,
            lineHeight: "32px",
            margin: "0 0 16px"
          })}
        >
          Login to{" "}
          <span className={css({ color: "#405dcf", fontSize: 32 })}>
            clique.
          </span>
        </h1>
      </Flex>

      <Flex>
        <FlexItem>
          <InputWithLabel type="text" label="Email" />
          <InputWithLabel type="text" label="Password" />

          <Button className={css({ width: "100%", marginTop: 12 })}>
            Log In
          </Button>
          <Paragraph
            className={css({
              margin: "16px 0"
            })}
          >
            <Link href="/accounts/reset-password">
              Having trouble logging in?
            </Link>
          </Paragraph>
          <Button color="secondary" className={css({ width: "100%" })}>
            Need to create a new account?
          </Button>
        </FlexItem>

        <FlexItem className={css({ paddingLeft: 24 })}>
          <Card>
            <CardBody>
              <Friends />
              <article
                className={css({
                  maxWidth: 280,
                  margin: "24px auto 0"
                })}
              >
                <Paragraph
                  className={css({
                    marginBottom: 8,
                    textAlign: "center"
                  })}
                >
                  Clique helps you plan events with your friends.
                </Paragraph>
                <Paragraph
                  className={css({
                    color: "rgba(0, 0, 0, 0.56)",
                    textAlign: "center"
                  })}
                >
                  Login, create an event, and start planning get togethers
                  easily.
                </Paragraph>
              </article>
            </CardBody>
          </Card>
        </FlexItem>
      </Flex>
    </MaxWidth>
  );
}
