import React from "react";
import { css } from "emotion";
import Paragraph from "./Paragraph";
import { Flex, FlexAuto, FlexItem } from "./Flex";

export default function ListItem({ title, avatar, children, subtitle, isFirst, isLast }) {
  let border = {};

  if (isFirst) {
    border = {
      borderTopLeftRadius: 4,
      borderTopRightRadius: 4
    };
  } else if (isLast) {
    border = {
      borderBottomLeftRadius: 4,
      borderBottomRightRadius: 4
    };
  }
  return (
    <Flex
      className={css({
        cursor: "pointer",
        padding: "16px",
        backgroundColor: "white",
        borderBottom: isLast ? "none" : "1px solid rgba(42, 51, 83, 0.12)",
        ":hover": {
          backgroundColor: "#dfe4e7"
        },
        ...border
      })}
    >
      <FlexAuto align="center">
        <div
          className={css({
            height: 48,
            width: 48,
            display: "flex",
            borderRadius: 4,
            overflow: "hidden"
          })}
        >
          <img
            alt="user profile"
            className={css({
              maxWidth: "100%",
              height: "auto",
              borderRadius: 4
            })}
            src={avatar}
          />
        </div>
      </FlexAuto>
      <FlexItem align="center">
        <div className={css({ padding: "0 16px" })}>
          <h3 className={css({ margin: "0 0 4px", fontSize: 16 })}>{title}</h3>
          {subtitle && <Paragraph>{subtitle}</Paragraph>}
          {children}
        </div>
      </FlexItem>
    </Flex>
  );
}
