import React from "react";
import { css } from "emotion";
import Paragraph from "../../core/Paragraph";
import MaxWidth from "../../core/MaxWidth";
import Card from "../../core/Card";
import CardBody from "../../core/CardBody";
import { Flex, FlexItem } from "../../core/Flex";
import Input from "../../core/Input";

export function Login() {
  return (
    <MaxWidth className={css({ width: "100%" })}>
      <Flex>
        <h1
          className={css({
            fontSize: 24,
            lineHeight: "32px",
            margin: "0 0 16px"
          })}
        >
          Login to clique
        </h1>
      </Flex>
      <Card>
        <CardBody>
          <Flex>
            <FlexItem>
              <Input type="text" />
              <Input type="text" />
            </FlexItem>

            <FlexItem className={css({ paddingLeft: 16 })}>
              <Paragraph>
                Clique helps you coordinate fun events with your friends
              </Paragraph>
            </FlexItem>
          </Flex>
        </CardBody>
      </Card>
    </MaxWidth>
  );
}
