import styled from "react-emotion";

const colorMap = {
  primary: '#5978f3',
  secondary: '#F5F5F5',
}

export default styled("button")`
  background-color: ${props => colorMap[props.color]};
  height 40px;
  border-radius: .25rem;
  font-weight: 700;
  color: white;
  cursor: pointer;
  border: none;
  padding: 11px 16px;
  font-size: 16px;
  &: hover {
    background-color: #6f8bfb;
  }
`;
