import React from "react";
import { css } from "emotion";
import Friends from "../../icons/Friends";
import Link from "../../core/Link";
import Paragraph from "../../core/Paragraph";
import MaxWidth from "../../core/MaxWidth";
import Card from "../../core/Card";
import CardBody from "../../core/CardBody";
import { Flex, FlexItem } from "../../core/Flex";
import Input from "../../core/Input";

export function Login() {
  return (
    <MaxWidth maxWidth="640" className={css({ width: "100%" })}>
      <Flex>
        <h1
          className={css({
            fontSize: 24,
            lineHeight: "32px",
            margin: "0 0 16px"
          })}
        >
          Login to clique
        </h1>
      </Flex>
      <Card>
        <CardBody>
          <Flex>
            <FlexItem>
              <Input type="text" placeholder="Email" />
              <Input type="text" placeholder="Password" />

              <Link>Having trouble logging in?</Link>
            </FlexItem>

            <FlexItem className={css({ paddingLeft: 24 })}>
              <Friends />
              <article
                className={css({
                  maxWidth: 280,
                  margin: "24px auto 0"
                })}
              >
                <Paragraph
                  className={css({
                    marginBottom: 8,
                    textAlign: "center"
                  })}
                >
                  Clique helps you plan events with your friends.
                </Paragraph>
                <Paragraph
                  className={css({
                    color: "rgba(0, 0, 0, 0.56)",
                    textAlign: "center"
                  })}
                >
                  Login, create an event, and start planning get togethers
                  easily.
                </Paragraph>
              </article>
            </FlexItem>
          </Flex>
        </CardBody>
      </Card>
    </MaxWidth>
  );
}
